mapper.update:
UPDATE  /* SamplePosMapper.updateSamplePosData - 샘플 POS 데이터 처리결과 단건 update */
    POS_IF_TABLE1
SET PROC_DT = SYSDATE()
WHERE 1=1
AND POS_SEQ = #{posSeq}
AND COLUMN1 = #{column1}

update.foreach:
UPDATE /* SamplePosMapper.updateSamplePosDataList - 샘플 POS 데이터 처리결과 다건 update */
    POS_IF_TABLE1
SET PROC_DT = SYSDATE()
WHERE 1=1
AND POS_SEQ = #{item.posSeq}
AND COLUMN1 = #{item.column1}
