mapper.insert:
INSERT INTO POINT_TABLE1 (  /* SamplePointMapper.insertSampleData - 샘플 포인트 데이터 등록 */
      COLUMN1   /* 컬럼1 */
    , COLUMN2   /* 컬럼2 */
    , INSERT_DT /* 등록일시 */
    , UPDATE_DT /* 변경일시 */
) VALUES (
      #{column1}
    , #{column2}
    , SYSDATE
    , SYSDATE
)

insert.foreach:
/* SamplePointMapper.insertSampleDataList - 샘플 포인트 데이터 대량 등록 */
INTO POINT_TABLE1 (
      COLUMN1   /* 컬럼1 */
    , COLUMN2   /* 컬럼2 */
    , INSERT_DT /* 등록일시 */
    , UPDATE_DT /* 변경일시 */
) VALUES (
      #{item.column1}
    , #{item.column2}
    , SYSDATE
    , SYSDATE
)
